import CardPelicula from '../../components/CardPelicula/CardPelicula';
import React, { Component } from 'react';

class Series extends Component {
  constructor(props) {
    super(props);
    this.state = {
      series: [],
      seriesOriginales: [],
      urlSeries: `https://api.themoviedb.org/3/tv/popular?api_key=7f7f8af8dc7e7a53c53410d1521c094f`,
      cargando: true,
      pagina: 1,
      input: '',
    };
  }

  componentDidMount() {
    this.obtenerSeries();
  }

  obtenerSeries = () => {
    fetch(`${this.state.urlSeries}&page=${this.state.pagina}`)
    .then(res => res.json())
    .then(data => {
      this.setState(prevState => ({
        series: prevState.series.concat(data.results),
        seriesOriginales: prevState.seriesOriginales.concat(data.results),
        cargando: false,
        pagina: prevState.pagina + 1,
      }));
    })
    .catch(error => {
      console.log('Error al cargar series:', error);
      this.setState({ cargando: false });
    });
  };

  manejarCambios = (event) => {
    const input = event.target.value;
    const seriesFiltradas = this.state.seriesOriginales.filter(serie =>
      serie.name.toLowerCase().includes(input.toLowerCase())
    );
    this.setState({
      series: seriesFiltradas,
      input: input,
    });
  };

  render() {
    const { series, cargando, input } = this.state;

     if (cargando) {
       return <h2>Cargando...</h2>;
     }

     return (
        <React.Fragment>

        <h2>Todas las series</h2>

        <form className="buscador">
          <input 
            type='text' 
            placeholder="Buscar una serie..." 
            onChange={this.manejarCambios} 
            value={input}
          />
        </form>

        <section className="row cards all-series" id="series">
            {series.length > 0 ? (
                series.map(serie => (
                  <CardPelicula 
                    key={serie.id}
                    id={serie.id}
                    titulo={serie.name}
                    imagen={serie.poster_path}
                    descripcion={serie.overview}
                    tipo="serie"
                  />
                ))
            ) : (
                <p>No se encontraron series que coincidan.</p>
            )}
        </section>

        <button onClick={this.obtenerSeries} className="btn btn-warning">Cargar m√°s</button>

        </React.Fragment>
     );
  }
}

export default Series;
