import React, { Component } from 'react';
import CardPelicula from '../../components/CardPelicula/CardPelicula'; // Asegúrate de la ruta correcta

class Favorites extends Component {
  constructor(props) {
    super(props);
    this.state = {
      favoritos: [],
    };
  }

  componentDidMount() {
    this.fetchFavorites();
  }

  fetchFavorites = () => {
    let datosEnLocalStorage = localStorage.getItem('LSfavoritos');
    let favoritos = [];
    if (datosEnLocalStorage != null) {
      favoritos = JSON.parse(datosEnLocalStorage);
    }
    this.setState({ favoritos });
  };

  // Opcional: Para actualizar la lista cuando se quita un favorito.
  componentDidUpdate(prevProps, prevState) {
    if (prevState.favoritos.length !== this.state.favoritos.length) {
      this.fetchFavorites();
    }
  }

  render() {
    const { favoritos } = this.state;
    const peliculasFavoritas = favoritos.filter(item => item.tipo === 'movie');
    const seriesFavoritas = favoritos.filter(item => item.tipo === 'serie');

    return (
      <>
        <h2 className="alert alert-primary">Mis Películas Favoritas</h2>
        <section className="row cards">
          {peliculasFavoritas.length > 0 ? (
            peliculasFavoritas.map(pelicula => (
              <CardPelicula 
                key={pelicula.id}
                id={pelicula.id}
                titulo={pelicula.titulo}
                imagen={pelicula.imagen}
                descripcion={pelicula.descripcion}
                tipo={pelicula.tipo}
              />
            ))
          ) : (
            <p>No tienes películas favoritas todavía.</p>
          )}
        </section>

        <h2 className="alert alert-primary">Mis Series Favoritas</h2>
        <section className="row cards">
          {seriesFavoritas.length > 0 ? (
            seriesFavoritas.map(serie => (
              <CardPelicula 
                key={serie.id}
                id={serie.id}
                titulo={serie.titulo}
                imagen={serie.imagen}
                descripcion={serie.descripcion}
                tipo={serie.tipo}
              />
            ))
          ) : (
            <p>No tienes series favoritas todavía.</p>
          )}
        </section>
      </>
    );
  }
}

export default Favorites;
