import React, { Component } from 'react';
import CardPelicula from '../../components/CardPelicula/CardPelicula';

class Resultados extends Component {
  constructor(props) {
    super(props);
    this.state = {
      resultados: [],
      cargando: true,
      query: '',
      tipoBusqueda: '',
    };
  }

  componentDidMount() {
    this.obtenerBusqueda();
  }

  componentDidUpdate(prevProps) {
    if (this.props.location.search !== prevProps.location.search) {
      this.obtenerBusqueda();
    }
  }

  obtenerBusqueda = () => {
    const apiKey = '7f7f8af8dc7e7a53c53410d1521c094f';
    const queryParams = new URLSearchParams(this.props.location.search);
    const query = queryParams.get('search');
    const tipo = queryParams.get('type') || 'movie'; // Por defecto busca películas si no se especifica
    const url = `https://api.themoviedb.org/3/search/${tipo}?api_key=${apiKey}&query=${query}`;

    this.setState({
      cargando: true,
      query: query,
      tipoBusqueda: tipo,
    });

    fetch(url)
      .then(res => res.json())
      .then(data => {
        this.setState({
          resultados: data.results,
          cargando: false,
        });
      })
      .catch(err => console.log('Error al buscar:', err));
  };

  render() {
    const { resultados, cargando, query, tipoBusqueda } = this.state;

    if (cargando) {
      return <h2>Cargando...</h2>;
    }

    if (resultados.length === 0) {
      return <h2 className="text-center my-5">No se encontraron resultados para "{query}".</h2>;
    }

    return (
      <>
        <h2 className="alert alert-info">Resultados para: "{query}"</h2>
        <section className="row cards">
          {resultados.map(item => (
            <CardPelicula 
              key={item.id}
              id={item.id}
              titulo={tipoBusqueda === 'movie' ? item.title : item.name}
              imagen={item.poster_path}
              descripcion={item.overview}
              tipo={tipoBusqueda}
            />
          ))}
        </section>
      </>
    );
  }
}

export default Resultados;
