import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import '../../styles/styles.css';

class CardPelicula extends Component {
  constructor(props) {
    super(props);
    this.state = {
      verDescripcion: false,
      textoBoton: 'Ver descripción',
      esFavorito: false,
    };
  }

  componentDidMount() {
    const id = this.props.id;
    let favoritos = JSON.parse(localStorage.getItem('LSfavoritos')) || [];
    const yaEsta = favoritos.some(item => item.id === id); 
    this.setState({ esFavorito: yaEsta });
  }

  manejarDescripcion = () => {
    this.setState(prevState => ({
      verDescripcion: !prevState.verDescripcion,
      textoBoton: prevState.verDescripcion ? 'Ver descripción' : 'Ocultar descripción',
    }));
  };

  agregarAFavoritos = () => {
    let favoritos = JSON.parse(localStorage.getItem('LSfavoritos')) || [];
    const { id, titulo, imagen, descripcion, tipo } = this.props;

    favoritos.push({ id, titulo, imagen, descripcion, tipo }); 
    localStorage.setItem('LSfavoritos', JSON.stringify(favoritos));
    this.setState({ esFavorito: true });
  };

  quitarDeFavoritos = () => {
    let favoritos = JSON.parse(localStorage.getItem('LSfavoritos')) || [];
    const id = this.props.id;

    const favoritosActualizados = favoritos.filter(item => item.id !== id);
    localStorage.setItem('LSfavoritos', JSON.stringify(favoritosActualizados));
    this.setState({ esFavorito: false });
  };

  render() {
    const { id, titulo, imagen, descripcion, tipo, onEliminar } = this.props;
    const urlImagen = imagen ? `https://image.tmdb.org/t/p/w500/${imagen}` : null;

    return (
      <article className="single-card-movie">
        {urlImagen ? (
          <img src={urlImagen} className="card-img-top" alt={titulo} />
        ) : (
          <div className="no-image">Sin imagen</div>
        )}

        <div className="cardBody">
          <h5 className="card-title">{titulo}</h5>

          <section className="section-desc">
            {this.state.verDescripcion && (
              <p className="card-text">{descripcion}</p>
            )}

            <button className="btn btn-primary" onClick={this.manejarDescripcion}>
              {this.state.textoBoton}
            </button>

            <Link to={`/${tipo}/${id}`} className="btn btn-primary">
              Ir a detalle
            </Link>

            {this.state.esFavorito ? (
              <button className="btn alert-primary" onClick={this.quitarDeFavoritos} title="Quitar de favoritos">
                ♥️
              </button>
            ) : (
              <button className="btn alert-primary" onClick={this.agregarAFavoritos} title="Agregar a favoritos">
                ♡
              </button>
            )}

            {onEliminar && (
              <button className="btn alert-danger" onClick={() => onEliminar(id)} title="Borrar de favoritos">
                Borrar
              </button>
            )}
          </section>
        </div>
      </article>
    );
  }
}

export default CardPelicula;
