import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';

class SearchForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      valorBuscador: '',
      tipoBusqueda: 'movie', // 'movie' o 'tv'
    };
  }

  manejarCambios = (evento) => {
    this.setState({ valorBuscador: evento.target.value });
  };

  manejarTipoBusqueda = (evento) => {
    this.setState({ tipoBusqueda: evento.target.value });
  };

  manejarEnvio = (evento) => {
    evento.preventDefault();
    const { valorBuscador, tipoBusqueda } = this.state;
    const url = `/results?search=${valorBuscador}&type=${tipoBusqueda}`;

    this.props.history.push(url);
  };

  render() {
    return (
      <form onSubmit={this.manejarEnvio} className="search-form" method="get">
        <input 
          type="text" 
          name="searchData" 
          placeholder="Buscar..." 
          value={this.state.valorBuscador}
          onChange={this.manejarCambios}
        />
        <select onChange={this.manejarTipoBusqueda} value={this.state.tipoBusqueda}>
          <option value="movie">Pel√≠culas</option>
          <option value="tv">Series</option>
        </select>
        <button type="submit" className="btn btn-success btn-sm">Buscar</button>
      </form>
    );
  }
}

export default withRouter(SearchForm);
