import React, { Component } from 'react';

class SerieDetail extends Component {
  constructor(props) {
    super(props);
    this.state = {
      serie: {},
      cargando: true,
      esFavorito: false,
    };
  }

  componentDidMount() {
    const apiKey = '';
    const id = this.props.match.params.id;
    const url = `https://api.themoviedb.org/3/tv/${id}?api_key=${apiKey}`;

    fetch(url)
      .then(res => res.json())
      .then(data => {
        this.setState({
          serie: data,
          cargando: false,
        });
      })
      .catch(err => console.log('Error al obtener datos de la serie:', err));

    let favoritos = JSON.parse(localStorage.getItem('LSfavoritos')) || [];
    const yaEsta = favoritos.some(item => item.id === parseInt(id)); 
    if (yaEsta) {
      this.setState({ esFavorito: true });
    }
  }

  agregarAFavoritos = () => {
    const { serie } = this.state;
    let favoritos = JSON.parse(localStorage.getItem('LSfavoritos')) || [];
    favoritos.push({
      id: serie.id,
      titulo: serie.name,
      imagen: serie.poster_path,
      descripcion: serie.overview,
      tipo: 'serie',
    });
    localStorage.setItem('LSfavoritos', JSON.stringify(favoritos));
    this.setState({ esFavorito: true });
  };

  quitarDeFavoritos = () => {
    const { serie } = this.state;
    let favoritos = JSON.parse(localStorage.getItem('LSfavoritos')) || [];
    const favoritosActualizados = favoritos.filter(item => item.id !== serie.id);
    localStorage.setItem('LSfavoritos', JSON.stringify(favoritosActualizados));
    this.setState({ esFavorito: false });
  };

  render() {
    const { serie, cargando, esFavorito } = this.state;

    if (cargando) return <h2>Cargando...</h2>;

    const corazonClase = esFavorito ? '♥️' : '♡';

    return (
      <section className="detail-movie">
        <img className="col-md-6" src={`https://image.tmdb.org/t/p/w500/${serie.poster_path}`} alt={serie.name} />
        <section className="detail-movieadad">
          <h3>Descripción</h3>
          <h2 className="alert alert-warning">{serie.name}</h2>
          <p className="description">{serie.overview}</p>
          <p id="release-date"><strong>Fecha de estreno:</strong> {serie.first_air_date}</p>
          <p id="episodes"><strong>Número de episodios:</strong> {serie.number_of_episodes}</p>
          <p id="seasons"><strong>Número de temporadas:</strong> {serie.number_of_seasons}</p>
          <p id="genres"><strong>Géneros:</strong> {serie.genres.map(g => g.name).join(', ')}</p>
        </section>
        {esFavorito ? (
          <button className="btn alert-primary" onClick={this.quitarDeFavoritos} title="Quitar de favoritos">
            Quitar de Favoritos {corazonClase}
          </button>
        ) : (
          <button className="btn alert-primary" onClick={this.agregarAFavoritos} title="Agregar a favoritos">
            Agregar a Favoritos {corazonClase}
          </button>
        )}
      </section>
    );
  }
}

export default SerieDetail;
